INSERT INTO `t_business_back` VALUES ('29', '停业登记', '28', '1', '【业务描述】<br />\r\n&nbsp;&nbsp;&nbsp; 实行定期定额征收的纳税人发生停业的，在停业前向主管税务机关申报办理停业登记；已办理停业登记但是停业期满不能及时恢复生产经营的纳税人，向税务机关提出延长停业登记。<br />\r\n&nbsp;&nbsp;&nbsp; 【报送资料】<br />\r\n&nbsp;&nbsp;&nbsp; (1)《停业复业（提前复业）报告书》。<br />\r\n&nbsp;&nbsp;&nbsp; (2)税务登记证及其副本。<br />\r\n&nbsp;&nbsp;&nbsp; (3)《发票领用簿》及未使用完的发票。<br />\r\n&nbsp;&nbsp;&nbsp; 【基本流程】<br />\r\n<img alt=\"\" src=\"/menucms/uploads/allimg/141114/1-1411141IT5G5.jpg\" /><br />\r\n&nbsp;&nbsp;&nbsp; 【基本规范】<br />\r\n&nbsp;&nbsp;&nbsp; (1)办税服务厅接收资料，核对纳税人报送资料是否齐全、是否符合法定形式，符合的即时办结；不符合的当场一次性告知纳税人应补正资料或不予受理原因。<br />\r\n&nbsp;&nbsp;&nbsp; (2)根据纳税人报送的资料，制作《停业复业（提前复业）报告书》交纳税人签字确认，纳税人免予填写表单。<br />\r\n&nbsp;&nbsp;&nbsp; (3)结清税款、滞纳金、罚款，收存税务登记证及其副本、《发票领用簿》及未使用完的发票。<br />\r\n&nbsp;&nbsp;&nbsp; (4)录入停业登记内容，发放《税务事项通知书》。<br />\r\n&nbsp;&nbsp;&nbsp; (5)1个工作日内将资料转下一环节调查核实。<br />\r\n&nbsp;&nbsp;&nbsp; 【升级规范】<br />\r\n&nbsp;&nbsp;&nbsp; 提供互联网络停业登记预申请服务。<br />\r\n&nbsp;', '0');
